const stderr = $input.first().json.stderr || '';
const match = stderr.match(/\/data\/media\/(boss_[^']+\.jpg)/);
const filename = match ? match[1] : 'unknown.jpg';
const filepath = match ? match[0] : '/data/media/unknown.jpg';

// Insert 노드에서 생성된 row의 id 가져오기
const insertedId = $('Update rows in a table1').item.json.id;

return [{ json: { id: insertedId, filename, filepath, exitCode: $input.first().json.exitCode } }];

// Loop over input items and add a new field called 'myNewField' to the JSON of each one
for (const item of $input.all()) {
  item.json.boss = "eraser";
  item.json.hero = "pencil"
}

return $input.all();

